import React, {useState} from 'react';
import Lists from './Lists';
import {Link, Redirect} from 'react-router-dom';
import {
  createCompletedList,
  addItemToCompletedLists,
  deleteDoc,
} from '../../store/actions/listActions';
import {firestoreConnect} from 'react-redux-firebase';
import {compose} from 'redux';
import {connect} from 'react-redux';
import {
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
} from 'mdbreact';

const AllLists = (props) => {
  console.log(props);
  let {auth} = props;
  const [lists, setLists] = useState(props.lists);
  console.log(props.lists);
  if (!auth.uid) return <Redirect to='/signin' />;
  function compare(a, b) {
    // Use toUpperCase() to ignore character casing
    const bandA = a.title.toUpperCase();
    const bandB = b.title.toUpperCase();

    let comparison = 0;
    if (bandA > bandB) {
      comparison = -1;
    } else if (bandA < bandB) {
      comparison = 1;
    }
    return comparison;
  }
  const sayHi = (e) => {
    console.log(e.target.id);
    lists = lists.slice().sort(compare);
    console.log(lists.slice().sort(compare));
  };
  return (
    <div>
      <MDBDropdown>
        <MDBDropdownToggle caret color='primary'>
          MDBDropdown
        </MDBDropdownToggle>
        <MDBDropdownMenu basic>
          <MDBDropdownItem id='TitleAsc' onClick={sayHi}>
            Action
          </MDBDropdownItem>
          <MDBDropdownItem>Another Action</MDBDropdownItem>
          <MDBDropdownItem>Something else here</MDBDropdownItem>
          <MDBDropdownItem divider />
          <MDBDropdownItem>Separated link</MDBDropdownItem>
        </MDBDropdownMenu>
      </MDBDropdown>
      {lists &&
        lists.map((list) => {
          // console.log(list);
          return (
            <>
              <Link to={`list/${list.id}`} key={list.id}>
                <Lists list={list} />
              </Link>
            </>
          );
        })}
    </div>
  );
};

const mapDispatchToProps = (dispatch) => {
  return {
    createCompletedList: (list) => dispatch(createCompletedList(list)),
    addItemToCompletedLists: (items) =>
      dispatch(addItemToCompletedLists(items)),
    deleteDoc: (items) => dispatch(deleteDoc(items)),
  };
};
const mapStateToProps = (state) => {
  console.log(state);
  return {
    auth: state.firebase.auth,
  };
};
export default compose(
  firestoreConnect(() => ['CompletedLists']),
  connect(mapStateToProps, mapDispatchToProps)
)(AllLists);
