import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
import CompletedItem from './CompletedItem';
const CompletedList = props => {
	// console.log(props.list); // to see match.params.id
	const { list, items } = props;
	console.log(props);

	const id = props.match.params.id;
	const [name, setName] = useState('');
	const [amount, setAmount] = useState(0);
	const [fetchedItems, setFetchedItems] = useState(items);

	useEffect(() => {
		setFetchedItems(items);
	}, [items]);
	if (list) {
		return (
			<div>
				<span>
					{props.list.title}-{id}
				</span>
				<div>
                {items &&
						items.map(item => (
							<CompletedItem
								key={item.id}
								item={item}
								id={id}
								handleClick={handleDeleteItem}
								handleEdit={handleEditItem}
							/>
						))}
				</div>
			</div>
		);
	} else {
		return <p>Loading...</p>;
	}
};
const mapStateToProps = (state, ownProps) => {
	// console.log(state);
	const id = ownProps.match.params.id;
	const CompletedLists = state.firestore.data.CompletedLists;
	const list = CompletedLists ? CompletedLists[id] : null;
	// console.log(lists, id);
	const items = state.firestore.data.CompletedLists
		? state.firestore.data.CompletedLists[id].items
		: null;
	console.log(items, list);
	return {
		list: list,
		items: items,
		auth: state.firebase.auth,
	};
};

export default compose(
	firestoreConnect(() => ['CompletedLists']),
	connect(mapStateToProps)
)(CompletedList);
