import React,{useState} from 'react'


const Items = (props) => {
    // console.log(props)
    const [itemName,setItemName] = useState(props.item.name)
    const [itemAmount,setItemAmount] = useState(props.item.amount)
    return (
        <div>
           

    <form onSubmit={console.log}>
            <div>
           
              <label htmlFor="name">Item: </label>
              <input type="text" id="name" value={itemName} onChange={event => {event.preventDefault(), setItemName(  event.target.value )}}  placeholder={itemName} />
            </div>
            <div>
              <label htmlFor="number">Amount</label>
              <input type="number" id="amount" value={itemAmount} onChange={event => {event.preventDefault(), setItemAmount(  event.target.value )}}  placeholder={itemAmount} />
            </div>
            <button onClick={()=>props.handleClick(props.id,props.item.id,props.item.name,props.item.amount)}>DELETE ITEM</button>
            <button onClick={()=>props.handleEdit(props.id,props.item.id,props.item.name,props.item.amount)}>Edit item</button>

          </form>
        </div>
    )
}


export default Items
