import React from 'react';
import moment from 'moment';
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBCol,
} from 'mdbreact';

const Lists = (props) => {
  console.log(props);
  const data = moment(props.list.createdAt.toDate()).calendar();
  console.log(props.list.items.length);
  const handleToggleComplete = () => {
    const {id, title, items} = props.list;
    // console.log(title, id);
    props.createCompletedList({title: title, listId: id});

    items &&
      items.forEach((item) => {
        props.addItemToCompletedLists({item: item, listId: id});
      });
    props.deleteDoc(id);
  };
  return (
    <MDBCol>
      <MDBCard style={{width: '22rem'}}>
        <MDBCardImage
          className='img-fluid'
          src='https://mdbootstrap.com/img/Photos/Others/images/43.jpg'
          waves
        />
        <MDBCardBody>
          <MDBCardTitle>{props.list.title}</MDBCardTitle>
          <MDBCardText>
            This list was created {data} and has {props.list.items.length} items
          </MDBCardText>
          <MDBBtn onClick={handleToggleComplete} href='#'>
            MDBBtn
          </MDBBtn>
        </MDBCardBody>
      </MDBCard>
    </MDBCol>
  );
};

export default Lists;
