import React, { useState } from 'react';
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
const List = (props) => {
  console.log(props.list); // to see match.params.id
  const { list } = props;
  const [name, setName] = useState('');
  const [amount, setAmount] = useState(0);
  const handleChangeName = (event)=>{
setName(event.t)
  }




  const id = props.match.params.id;
  if (list) {
    return (
      <div>
        <span>
          {props.list.title}-{id}
        </span>
        <div>
          <form onSubmit={console.log}>
            <h5>Create list</h5>
            <div>
              <label htmlFor="name">Name</label>
              <input type="text" id="name" onChange={console.log} />
            </div>
            <div>
              <label htmlFor="number">Amount</label>
              <input type="number" id="amount" onChange={console.log} />
            </div>

            <div>
              <button>Add item</button>
            </div>
          </form>
        </div>
      </div>
    );
  } else {
    return <p>Loading...</p>;
  }
};
const mapStateToProps = (state, ownProps) => {
  // console.log(ownProps);
  const id = ownProps.match.params.id;
  const lists = state.firestore.data.lists;
  const list = lists ? lists[id] : null;
  return {
    list: list,
    auth: state.firebase.auth,
  };
};

export default compose(
  connect(mapStateToProps),
  firestoreConnect((props) => [{ collection: 'list', doc: props.match.params.id }])
)(List);
