import React, {useState} from 'react';
import {
  MDBBtn,
  MDBCol,
  MDBRow,
  MDBIcon,
  MDBContainer,
  MDBInput,
  MDBListGroupItem,
  MDBListGroup,
} from 'mdbreact';

const Items = (props) => {
  // console.log(props)
  const [itemName, setItemName] = useState(props.item.name);
  const [itemAmount, setItemAmount] = useState(props.item.amount);
  // console.log(props);
  return (
    <div style={{width: '400px', margin: 'auto', marginTop: '60px'}}>
      <MDBContainer>
        <MDBListGroup className='my-4 mx-4' style={{width: '22rem'}}>
          <MDBListGroupItem color='primary'>
            <MDBInput
              label='Item Name'
              id='name'
              onChange={(event) => setItemName(event.target.value)}
              id='name'
              value={itemName}
              placeholder={itemName}
            />
            <MDBInput
              label='Amount'
              id='amount'
              onChange={(event) => setItemAmount(event.target.value)}
              id='amount'
              value={itemAmount}
              placeholder={itemAmount}
            />
          </MDBListGroupItem>
          <MDBListGroupItem className='d-flex justify-content-between align-items-center'>
            Cras justo odio
            <MDBBadge color='primary' pill>
              14
            </MDBBadge>
          </MDBListGroupItem>
        </MDBListGroup>
      </MDBContainer>

      <form>
        <MDBBtn
          color='mdb-color'
          onClick={() => {
            props.handleClick(props.id, props.item.id, itemName, itemAmount);
          }}>
          DELETE ITEM
        </MDBBtn>

        <MDBBtn
          color='mdb-color'
          onClick={() => {
            props.handleEdit(props.id, props.item.id, itemName, itemAmount);
          }}>
          Edit Item
        </MDBBtn>
      </form>
    </div>
  );
};

export default Items;

