import React from 'react';
import Lists from './Lists';
import { Link } from 'react-router-dom';
import { createCompletedList } from '../../store/actions/listActions';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
import { connect } from 'react-redux';

const AllLists = props => {
	// console.log(props);
	const { lists } = props;
	const handleToggleComplete = (listTitle, items) => {
		console.log(listTitle, items);
		createCompletedList({ title: listTitle });
	};
	return (
		<div>
			{lists &&
				lists.map(list => {
					console.log(list);
					return (
						<>
							<Link to={`list/${list.id}`} key={list.id}>
								<Lists list={list} />
							</Link>
							<button
								type='button'
								onClick={() => handleToggleComplete(list.title, list.items)}
							>
								click
							</button>
						</>
					);
				})}
		</div>
	);
};

const mapDispatchToProps = dispatch => {
	return {
		createCompletedList: list => dispatch(createCompletedList(list)),
	};
};
export default compose(
	firestoreConnect(() => ['lists']),
	connect(null, mapDispatchToProps)
)(AllLists);
