import React from 'react';
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
const List = (props) => {
  console.log(props.list); // to see match.params.id
  const { list } = props;

  const id = props.match.params.id;
  if (list) {
    return (
      <div>
        <span>
          {props.list.title}-{id}
        </span>
        <div>
          <form onSubmit={handleSubmit}>
            <h5>Create list</h5>
            <div>
              <label htmlFor="title">title</label>
              <input type="text" id="title" onChange={handleChangeTitle} />
            </div>

            <div>
              <button>create</button>
            </div>
          </form>
        </div>
      </div>
    );
  } else {
    return <p>Loading...</p>;
  }
};
const mapStateToProps = (state, ownProps) => {
  // console.log(ownProps);
  const id = ownProps.match.params.id;
  const lists = state.firestore.data.lists;
  const list = lists ? lists[id] : null;
  return {
    list: list,
    auth: state.firebase.auth,
  };
};

export default compose(
  connect(mapStateToProps),
  firestoreConnect((props) => [{ collection: 'list', doc: props.match.params.id }])
)(List);
