import React, { useState } from 'react';
import { connect } from 'react-redux';
import { createList } from '../../store/actions/listActions';
import { Redirect } from 'react-router-dom';

const CreatList = (props) => {
  const [title, setTitle] = useState('');
  const [itemName, setItemName] = useState('');
  const [itemAmount, setItemAmount] = useState(0);
  const { auth } = props;
  const handleChangeTitle = (event) => {
    setTitle(event.target.value);
  };
  const handleChangeItemName = (event) => {
    setItemName(event.target.value);
  };
  const handleChangeItemAmount = (event) => {
    setItemAmount(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    props.createList({ title: title, name: itemName, amount: itemAmount });
    props.history.push('/');
  };
  if (!auth.uid) return <Redirect to="/signin" />;
  return (
    <div className="container">
      <form onSubmit={handleSubmit} className="white">
        <h5 className="grey-text text-darken-3">Create list</h5>
        <div className="input-field">
          <label htmlFor="title">title</label>
          <input type="text" id="title" onChange={handleChangeTitle} />
        </div>
        <div className="input-field">
          <label htmlFor="name">Item Name</label>
          <input type="text" id="itemName" onChange={handleChangeItemName} />
        </div>
        <div className="input-field">
          <label htmlFor="amount">Amount</label>
          <input type="number" id="itemAmount" onChange={handleChangeItemAmount} />
        </div>
        <div className="input-field">
          <button className="btn pink light-1 z-depth-0">create</button>
        </div>
      </form>
    </div>
  );
};
const mapStateToProps = (state) => {
  console.log(state);
  return {
    auth: state.firebase.auth,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    createList: (list) => dispatch(createList(list)),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(CreatList);
