import React from 'react';
import AllLists from '../lists/AllLists';
import {connect} from 'react-redux';
import {compose} from 'redux';
import {firestoreConnect} from 'react-redux-firebase';
import {Redirect} from 'react-router-dom';
import {db} from '../../config/firebaseConfig';
import {orderCollection} from '../../store/actions/listActions';
const Dashboard = () => {
  const {lists, auth} = props;
  let test = db.collection('lists');
  console.log(props.orderCollection());
  if (auth.uid) {
    return (
      <div>
        <AllLists lists={lists} />
      </div>
    );
  } else {
    return <Redirect to='/signin' />;
  }
};
const mapStateToProps = (state) => {
  console.log(state);
  return {
    lists: state.firestore.ordered.lists,
    auth: state.firebase.auth,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    orderCollection: () => dispatch(orderCollection()),
  };
};
export default compose(
  connect(mapStateToProps, mapDispatchToProps),
  firestoreConnect(() => [
    {
      collection: 'lists',
      orderBy: ['title', 'asc'],
    },
  ])
)(Dashboard);
