import React from 'react';
import Lists from './Lists';
import { Link } from 'react-router-dom';
import { createCompletedList } from '../../store/actions/listActions';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
import { connect } from 'react-redux';

const AllLists = props => {
	// console.log(props);
	const { lists } = props;
	const handleToggleComplete = (itemName, itemAmount, itemID) => {
		console.log(itemName, itemAmount, itemID);
	};
	return (
		<div>
			{lists &&
				lists.map(list => {
					console.log(list);
					return (
						<>
							<Link to={`list/${list.id}`} key={list.id}>
								<Lists list={list} />
							</Link>
							<button type='button' onClick={() => handleToggleComplete()}>
								click
							</button>
						</>
					);
				})}
		</div>
	);
};

//**
// const mapDispatchToProps = dispatch => {
// 	return {
// 		addItem: list => dispatch(addItem(list)),
// 		deleteItem: item => dispatch(deleteItem(item)),
// 		editItem: item => dispatch(editItem(item)),
// 	};
// };
// export default compose(
// 	firestoreConnect(() => ['lists']),
// 	connect(mapStateToProps, mapDispatchToProps)
// )(List);
const mapDispatchToProps = dispatch => {
	return {
		createCompletedList: list => dispatch(createCompletedList(list)),
	};
};
export default compose(
	firestoreConnect(() => ['lists']),
	connect(mapStateToProps, mapDispatchToProps)
)(AllLists);
