import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
import { addItem, deleteItem, editItem } from '../../store/actions/listActions';
import Items from './Items';
const CompletedList = props => {
	// console.log(props.list); // to see match.params.id
	const { list, items } = props;
	console.log(props);

	const id = props.match.params.id;
	const [name, setName] = useState('');
	const [amount, setAmount] = useState(0);
	const [fetchedItems, setFetchedItems] = useState(items);

	useEffect(() => {
		setFetchedItems(items);
	}, [items]);
	if (list) {
		return (
			<div>
				<span>
					{props.list.title}-{id}
				</span>
				<div>
					<form>
						<h5>Add item</h5>
						<div>
							<label htmlFor='name'>Name</label>
							<input type='text' id='name' onChange={console.log} />
						</div>
						<div>
							<label htmlFor='number'>Amount</label>
							<input type='number' id='amount' onChange={console.log} />
						</div>
					</form>
				</div>
			</div>
		);
	} else {
		return <p>Loading...</p>;
	}
};
const mapStateToProps = (state, ownProps) => {
	console.log(state);
	const id = ownProps.match.params.id;
	const lists = state.firestore.data.CompletedLists;
	const list = CompletedLists ? lists[id] : null;
	// console.log(lists, id);
	const items = state.firestore.data.lists
		? state.firestore.data.lists[id].items
		: null;
	// console.log(items);
	return {
		list: list,
		items: items,
		auth: state.firebase.auth,
	};
};

export default compose(
	firestoreConnect(() => ['CompletedLists']),
	connect(mapStateToProps)
)(CompletedList);
