import React, {useState} from 'react';
import {
  MDBBtn,
  MDBCol,
  MDBRow,
  MDBIcon,
  MDBContainer,
  MDBInput,
  MDBListGroupItem,
  MDBListGroup,
  MDBBadge,
} from 'mdbreact';

const Items = (props) => {
  // console.log(props)
  const [itemName, setItemName] = useState(props.item.name);
  const [itemAmount, setItemAmount] = useState(props.item.amount);
  // console.log(props);
  return (
    <div style={{width: '400px', margin: 'auto', marginTop: '60px'}}>
      <MDBContainer>
        <MDBListGroup className='my-4 mx-4' style={{width: '22rem'}}>
          <MDBListGroupItem className='d-flex justify-content-between align-items-center'>
            {itemName}
            <MDBIcon
              icon='trash'
              onClick={() => {
                props.handleClick(
                  props.id,
                  props.item.id,
                  itemName,
                  itemAmount
                );
              }}
            />
            <MDBIcon icon='edit' />
            <MDBBadge color='primary' pill>
              {itemAmount}
            </MDBBadge>
          </MDBListGroupItem>
        </MDBListGroup>
      </MDBContainer>
    </div>
  );
};

export default Items;

